const serviceIconMap = new Map<string, Component>([
	['openai', OpenAIIcon],
	['anthropic', AnthropicIcon],
	['gemini', GeminiIcon],
	['groq', GroqIcon],
	['openrouter', OpenRouterIcon],
	['mistral', MistralIcon],
	['github', GithubIcon],
]);

export const getAIIconComponent = (
	name: string
): Component<JSX.SvgSVGAttributes<SVGSVGElement>> => {
	return serviceIconMap.get(name.toLowerCase()) || OllamaIcon;
};

export const AIIcon: Component<
	{ name: string } & JSX.SvgSVGAttributes<SVGSVGElement>
> = (props_) => {
	const [withName, props] = splitProps(props_, ['name']);
	return <Dynamic component={getAIIconComponent(withName.name)} {...props} />;
};
